mapper.insert:

insert into deliveryreceipt (d_startcity, d_destinationcity, d_arrivedcity, 
  d_status, d_createtime, d_remark
  )
values (#{dStartcity,jdbcType=VARCHAR}, #{dDestinationcity,jdbcType=VARCHAR}, #{dArrivedcity,jdbcType=VARCHAR}, 
  #{dStatus,jdbcType=INTEGER}, #{dCreatetime,jdbcType=TIMESTAMP}, #{dRemark,jdbcType=VARCHAR}
  )

mapper.select:

